import { Component } from "react";


class InputPengunjung extends Component {
    constructor(props){
        super(props)
        this.state ={
            nama : "",
            umur : "",
            jenisKelamin: "",
            editing : true
        }
    }

    onChange = e => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    handleBukaInput = () =>{
        this.setState({
            editing:false
        })
    }
    
    handleTutupInput = () =>{
        this.setState({
            editing:true
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();

        const isInputNotEmty = this.state.nama && this.state.umur && this.state.jenisKelamin;
