        }
    }


    render(){

        const viewMode = {};
        const editMode = {};

        (this.state.editing) ? viewMode.display = "none" : editMode.display = "None";

        return(
            <div>
                <form onSubmit={() => {}} style={viewMode}>
                    <label htmlFor="nama">Nama Pengunjung</label>
                    <input type="text" placeholder="Masukan Nama..." id="nama" name="nama" value={this.state.nama} onChange={onchange} /><br />

                    <label htmlFor="umur">Umur Pengunjung</label>
                    <input type="text" id="umur" placeholder="Masukan Umur..." name="umur" value={this.state.umur}  onChange={onchange}/><br />

                    <label htmlFor="jenis Kelamin">Jenis Kelamin</label>
                    <input type="text" id="jenisKelamin" placeholder="Masukan Gender..." name="jenisKelamin" value={this.state.jenisKelamin} onchange={onchange} /><br />

                    <button onSubmit={() => {}}>Input</button>
                    <button onClick={this.handleTutupInput}>Batal</button>
                </form>

                <button onClick={this.handleBukaInput} style={editMode}>Masukan Nama Pengunjung</button>
            </div>
        )
    }
}


export default InputPengunjung;
