import { Component } from "react";


class InputPengunjung extends Component {
    constructor(props){
        super(props)
        this.state ={
            nama : "",
            umur : "",
            jenisKelamin: "",
            editing : true
        }
    }

    handleBukaInput = () =>{
        this.setState({
            editing:false
        })
    }
    
    handleTutupInput = () =>{
        this.setState({
            editing:true
        })
    }


    render(){

        const viewMode = {};
        const editMode = {};

        (this.state.editing) ? viewMode.display = "none" : editMode.display = "None";

        return(
            <div>
                <form onSubmit={() => {}} style={viewMode}>
                    <label htmlFor="nama">Nama Pengunjung</label>
                    <input type="text" placeholder="Masukkan Nama..." id="nama" name="nama" value={this.state.nama} onChange={() => {}} />
                </form>

                <button onClick={this.handleBukaInput} style={editMode}>Masukan Nama Pengunjung</button>
            </div>
        )
    }
}


export default InputPengunjung;
