import NavbarPage from "./NavbarPage";
import HeaderPage from "./HeaderPage";
import { article } from "../article";
import FormPage from "./FormPage";
import TabelPage from "./TabelPage";
import { v4 as uuidv4 } from 'uuid';
import { useState } from "react";


const initialValue = [
    {
        id : uuidv4() ,
        nama : "Eiger",
        Kategori : "T-Shirt",
        image : "img.jpg",
        freshness : "New Brand",
        deskripsi: "suisusius",
        price : "2",
    },
    {
        id : uuidv4() ,
        nama : "Rei",
        Kategori : "Jacket",
        image : "img.jpg",
        freshness : "New Brand",
        deskripsi: "suisusius",
        price : "2",
    },
    {
        id : uuidv4() ,
        nama : "Erigo",
        Kategori : "Jacket",
        image : "img.jpg",
        freshness : "New Brand",
        deskripsi: "suisusius",
        price : "2",
    },
]

function Home () {

    const [data, setData] = useState(initialValue);
    


    const hapusProduk = (id) => {

        setData((oldData) => oldData.filter((produk) => {
            produk.id !== id;
        }))
    }

    tambahProduk = (produkBaru) => {
        const newProduk = { id : uuidv4(), ...produkBaru};

        console.log(newProduk.id);

        this.setState({data : [...this.state.data, newProduk]});

    }


        return(
            <>
            <NavbarPage />
            <HeaderPage article={article}/>
            <FormPage tambahProduk={this.tambahProduk} />
            <TabelPage datas={this.state.data} hapusProduk={this.hapusProduk}/>
            </>
        )
    
}

export default Home;
